(()=>{let e=document.querySelector("#menu-bar"),t=document.querySelector(".navbar"),o=document.querySelectorAll(".option");function c(e){o.forEach((e=>e.classList.remove("activeOption"))),e.target.classList.add("activeOption"),console.log(e.target.textContent),document.getElementById("content").innerHTML=""}e.onclick=()=>{e.classList.toggle("fa-times"),t.classList.toggle("active")},window.onscroll=()=>{e.classList.remove("fa-times"),t.classList.remove("active")},o.forEach((e=>e.addEventListener("click",c))),console.log("Houmser weno")})();